<?php

namespace Composer;

use Composer\Semver\VersionParser;






class InstalledVersions
{
private static $installed = array (
  'root' => 
  array (
    'pretty_version' => '1.0.0+no-version-set',
    'version' => '1.0.0.0',
    'aliases' => 
    array (
    ),
    'reference' => NULL,
    'name' => 'baumrock/pwstandalone',
  ),
  'versions' => 
  array (
    'baumrock/pwstandalone' => 
    array (
      'pretty_version' => '1.0.0+no-version-set',
      'version' => '1.0.0.0',
      'aliases' => 
      array (
      ),
      'reference' => NULL,
    ),
    'processwire/processwire' => 
    array (
      'pretty_version' => '3.0.165',
      'version' => '3.0.165.0',
      'aliases' => 
      array (
      ),
      'reference' => 'd8945198f4a6a60dab23bd0462e8a6285369dcb9',
    ),
    'tracy/tracy' => 
    array (
      'pretty_version' => 'v2.8.2',
